import React, { Button } from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBMedia, MDBAlert } from 'mdbreact';
import '../css/form.css';
import logo from "../assests/images/logo-placeholder.png"
import { Link } from "react-router-dom";
import AuthService from '../AuthService.js'
import { ToastConsumer, ToastProvider, withToastManager } from 'react-toast-notifications';

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            collapse: false,
            username: '',
            password: '',
            errUsr: 'd-none',
            errPwd: 'd-none',
            errErr: 'd-none',
        };
    }
    handleUsername(e) {
        this.setState({ username: e.target.value })
    }
    handlePassword(e) {
        this.setState({ password: e.target.value })
    }
    login(e, clientID, secret) {
        e.preventDefault();
        const { toastManager } = this.props;
        if (clientID.length === 0 || secret.length === 0) {
            toastManager.add(`Something went wrong: "Tosin error"`, {
                appearance: 'error',
            });
            this.setState({ errErr: 'd-none' })
            this.setState({ errPwd: 'd-none' })
            this.setState({ errUsr: 'd-block' })
            return 0;
        }
        // } else if (secret.length === 0) {
        //     toastManager.add(`Something went wrong: "${error.message}"`, {
        //         appearance: 'error',
        //       });
        //     this.setState({ errErr: 'd-none' })
        //     this.setState({ errPwd: 'd-block' })
        //     this.setState({ errUsr: 'd-none' })
        //     return 0;
        // }

        let url = 'https://staging.seerbitapigateway.com/FCMB_BACK/rest/api/oauth';
        let param = {
            clientID,
            secret
        };
        const options = {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                // 'Authorization': "eyJhbGciOiJIUzUxMiJ9.eyJjbGllbnRpZCI6InRlc3QiLCJzZWNyZXQiOiJ0ZXN0IiwiZXhwIjoxNTQ3NjM5ODQwLCJpc3MiOiJDZW50cmljIn0.lr_AgEfXCdM8clJM65Xl-5Ik49SHRI09zNsBV076QT-bUyPn29sCZhcf1SbrIlICFeEqkIrchfm80Nm2j2EdXw"
            },
            body: JSON.stringify(param),
        }
        fetch(url, options)
            .then((response) => response.json())
            .then(result => {
                if (result.response !== undefined) {
                    // this.setState({ errErr: 'd-block' })
                    // this.setState({ errPwd: 'd-none' })
                    // this.setState({ errUsr: 'd-none' })
                    toastManager.add(`Something went wrong: "${result}"`, {
                        appearance: 'error',
                    });
                } else {
                    AuthService.setToken(result.access_token)
                    this.props.history.push("/main")
                    toastManager.add('Saved Successfully', { appearance: 'success' });
                }
                return result;
            })
            .catch(err => {
                throw err;
            });
    }
    render() {
        this.Demo('alert')
        return (
            <div className="App">
                <header className="App-header">
                    <MDBContainer>
                        <MDBRow>
                            <MDBCol md="4"></MDBCol>
                            <MDBCol md="4">
                                <form onSubmit={(e) => { this.login(e, this.state.username, this.state.password) }}>
                                    <p className="h4 center mb-4">
                                        <MDBMedia center href="#">
                                            <MDBMedia object center src={logo} alt={"Placeholder"} className='logo-img' />
                                        </MDBMedia>
                                    </p>
                                    <MDBAlert color="warning" className={this.state.errErr}>
                                        <span className='close' style={{ fontSize: '12px', color: 'warning' }} onClick={() => this.setState({ errErr: 'd-none' })}>x</span>
                                        <strong style={{ fontSize: '12px', color: 'red' }}>Invalid Username and Password</strong>
                                    </MDBAlert>
                                    <Button onClick={() => toastManager.add("alert", {
                                        appearance: 'success',
                                        autoDismiss: true,
                                    })}>
                                        Add Toast
                                    </Button>
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder='Phone Number'

                                        validate
                                        error="wrong"
                                        success="right"
                                        onChange={this.handleUsername.bind(this)}
                                        value={this.state.username}
                                    />
                                    <span className={this.state.errUsr} onMouseMove={() => this.setState({ errUsr: 'd-none' })} style={{ fontSize: '12px', color: 'red' }} >Phone Number is required</span>
                                    <br />
                                    <input
                                        type="password"
                                        className="form-control actionColor"
                                        placeholder='Password'
                                        onChange={this.handlePassword.bind(this)}
                                        value={this.state.password}
                                    />
                                    <span className={this.state.errPwd} onMouseMove={() => this.setState({ errPwd: 'd-none' })} style={{ fontSize: '12px', color: 'red' }}>Password is required</span>
                                    <br />
                                    <div className="text-center">
                                        <MDBBtn color="purple" type="submit" className='largeInput gold-color myblue'>Login to your account</MDBBtn>
                                    </div>
                                    <div className="form-group text-center ">
                                        <a href="/password-reset">Forgot your Password?</a>
                                    </div>
                                    <div className="clearfix-50">
                                        <div className="form-group">
                                            <Link className="btn btn-block signup" to={`/registration-option`}>Create an Account</Link>
                                        </div>
                                    </div>
                                </form>
                            </MDBCol>
                        </MDBRow>
                    </MDBContainer>
                </header>
            </div>
        );
    }
};

export default withToastManager(Login);
